import axios from "axios";
import { useState, useEffect } from "react";
import { vendasTipoBebida } from "../../../types/sales";
import { springPage } from "../../../types/vendor/springPage";
import { isAuthenticated, getTokenData } from "../../../utils/auth";
import { BASE_URL } from "../../../utils/request";
import { getAuthData } from "../../../utils/storage";
import { formatPrice } from "../../../utils/format";

const OutrosRealizado = () => {

    const [metaOutrosRealizado, setOutrosRealizado] = useState<springPage<vendasTipoBebida>>();

    useEffect(() => {
        if (isAuthenticated()) {
            axios.get(`${BASE_URL}/vendas/vendas-paginada-por-tipodebebida?rota=${getTokenData()?.user_name}`,
                { headers: { Authorization: "Bearer " + getAuthData().access_token } })
                .then((response) => {
                    setOutrosRealizado(response.data);
                })
        };
    }, []);

    return (
            <div className="d-flex flex-wrap justify-content-center">
                {
                    metaOutrosRealizado?.content.map
                        (item =>

                                <div  key = {item.qtde_venda} className="card-body ">
                                    <div className="card text-primary"><span className="card-header mb-3 text-white bg-quarter">{item.tipo_bebida}</span>
                                        <span className="dropdown-item-text text-secondary">Realizado <br /> acumulado<h5 className="text-secondary">{formatPrice(item.qtde_venda)}</h5></span>
                                    </div>
                                </div>
                        )
                }
            </div>
    )
}
export default OutrosRealizado;
