import Footer from '../../components/Footer';
import NavBarRota from '../../components/NavBarRota';
import BarChartRotaFamilia from '../../components/BarChatRotaFamilia';
import BarChartRota from '../../components/BarChatRota';
import DonutChart2 from 'components/DonutChart2';
import { getTokenData, isAuthenticated, Tokendata } from 'utils/auth';
import { useEffect, useState } from 'react';
import DonutChart3 from 'components/DonutChart3';
import axios from 'axios';
import { BASE_URL } from 'utils/request';
import { getAuthData } from 'utils/storage';
import { VendaTotalPorRota } from 'types/sales';
import MetaDaRota from 'components/Metas/MetaDaRota';
import Metas from 'components/Metas';
import { springPage } from 'types/vendor/springPage';
import { formatPrice } from 'utils/format';
import './styles.css'

import DataCalc from "./../../components/DataCalc";

type AuthData = {
  authenticated: boolean,
  tokenData?: Tokendata
}

const Rotas = () => {
  const [authData, setAuthData] = useState<AuthData>({ authenticated: false });
  const [venda, setVenda] = useState<springPage<VendaTotalPorRota>>();

  useEffect(() => {
    if (isAuthenticated()) {

      setAuthData({
        authenticated: true,
        tokenData: getTokenData()
      });

      axios.get(`${BASE_URL}/vendas/total-vendas-rota?rota=${getTokenData()?.user_name}`,
        { headers: { Authorization: "Bearer " + getAuthData().access_token } })
        .then((response) => {
          setVenda(response.data);
        })
    } else {
      setAuthData({
        authenticated: false
      });
    };
  }, []);
  return (
    <>
      <NavBarRota />
      <div className='div-rota container'>
        <h1 className='text-secondary py-3 border-bottom'>Resumo do Vendedor <span className='text-primary'>{authData.tokenData?.user_name}</span></h1>
        <div className='row '>
          <div className='col-sm-6'>
            <h5 className='text-center text-secondary'>Volume por Familia</h5>
            <BarChartRotaFamilia />
          </div>
          <div className='col-sm-6'>
            <h5 className='text-center text-secondary'>Faturado por Familia</h5>
            <BarChartRota />
          </div>
          <div className='col-sm-6'>
            <h5 className='text-center text-secondary'>Volume por embalagem</h5>
            <DonutChart2 />
          </div>
          <div className='col-sm-6'>
            <h5 className='text-center text-secondary'>Faturado por embalagem</h5>
            <DonutChart3 />
          </div>
        </div>
        {/*///////////////////CONTAINER RESUMO DE VALORES/////////////*/}
        <div className=''>
          <div className='container text-primary text-center'>
            {/*////////////////////TOTAL VENDAS//////////////////////*/}

            <div className="card-body text-secondary">
              <p className="m-3"><strong>Nota:</strong> Os numeros aqui mostrados são referentes a 1(um) dia anterior ao mês atual do ano corrente.</p>
              <div className="d-flex flex-wrap justify-content-center">

                <div id="accordion">
                  {venda?.content.map(item =>
                    <div key={item.rota} className="card m-3">
                      <h5 className="card-header bg-light text-primary text-uppercase">Total faturado</h5>
                      <h3 className="m-3">{formatPrice(item.totalvenda)} </h3>
                    </div>
                  )}
                </div>
                <MetaDaRota />
                <DataCalc />
              </div>
            </div>
            <div className="text-secondary">
              <Metas />
            </div>
          </div>
        </div>
        {/* <DataTable3 />*/}
      </div>
      <Footer />
    </>
  );
};
export default Rotas;
