import axios from "axios";
import { useState, useEffect } from "react";
import { MetaTotalPorRota } from "./../../../types/sales";
import { springPage } from "./../../../types/vendor/springPage";
import { isAuthenticated, getTokenData } from "./../../../utils/auth";
import { BASE_URL } from "./../../../utils/request";
import { getAuthData } from "./../../../utils/storage";
import { formatPrice } from "./../../../utils/format";

const MetaDaRota = () => {

    const [metaDaRota, setMetaDaRota] = useState<springPage<MetaTotalPorRota>>();

    useEffect(() => {
        if (isAuthenticated()) {
            axios.get(`${BASE_URL}/metas/metas-total-por-rota?rota=${getTokenData()?.user_name}`,
                {
                    headers: { Authorization: "Bearer " + getAuthData().access_token }
                })
                .then((response) => {
                    setMetaDaRota(response.data);
                })
        }
    }, []);
    return (
            <div id="accordion">
                {metaDaRota?.content.map(item =>
                    <div key={item.rota} className="card mt-3">
                        <h5 className="card-header bg-light text-primary text-uppercase">Meta da rota</h5>
                        <h3 className="m-3">{formatPrice(item.valor)} </h3>
                    </div>
                )}
                </div>
    )
}

export default MetaDaRota;
