import axios from "axios";
import { useEffect, useState } from "react";
import { MetaRefrigerantePorEmbalagem } from "types/sales";
import { springPage } from "types/vendor/springPage";
import { isAuthenticated, getTokenData } from "utils/auth";
import { formatPrice } from "utils/format";
import { BASE_URL } from "utils/request";
import { getAuthData } from "utils/storage";

const VendaEmbRefri = () => {

    const [metaRefri, setMetaRefri] = useState<springPage<MetaRefrigerantePorEmbalagem>>();

    useEffect(() => {
        if (isAuthenticated()) {
            axios.get(`${BASE_URL}/metas-refrigerantes/realizado-por-embalagem?rota=${getTokenData()?.user_name}`,
                { headers: { Authorization: "Bearer " + getAuthData().access_token } })
                .then((response) => {
                    setMetaRefri(response.data);
                })
        };
    }, []);
    return (
        <div className="d-flex flex-wrap justify-content-center">
            {
                metaRefri?.content.map
                    (item =>
                            <div key = {item.embalagem} className="card-body">
                                    <div className="card text-primary "><span className="card-header text-white mb-3 text-primary">{item.embalagem}</span>
                                    <span className="dropdown-item-text text-secondary">Meta mensal <h5 className="text-secondary">{formatPrice(item.meta)}</h5></span>
                                    <span className="dropdown-item-text text-secondary">Meta di√°ria <h5 className="text-secondary">{formatPrice(item.meta / 26)}</h5></span>
                                    <span className="dropdown-item-text text-secondary">Realizado <br/> acumulado<h5 className="text-secondary">{formatPrice(item.realizado)}</h5></span>
                                    </div>
                            </div>
                    )
            }
        </div>
    )
}

export default VendaEmbRefri;
